function [gamInverse,dacsize] = LoadCalibrationFileRR(CalibrationFile, expdir, GeneralUseScripts)%LoadCalibrationFile.m is a function that loads and returns gamInverse & dacsize %from the calibrationFile.%This script is created by FT on 2000/05/10 and modified by JJ on 2007/06/28%Then, RR made this version on 2010/10/10 so that it will work on a pc, %where paths sopmetimes contain spaces. err = 0;eval(['cd ''' GeneralUseScripts '/Calibration''']);eval(['load ' CalibrationFile ' gamInverse dacsize'],'err = 1'); %Load gamInverse & dacsize from CalibrationFileif err	disp(['Warning! Calibration File Not Found!']);	query = input('Do you want to proceed using uncorrected gamma?  y or n(default) ??  ','s');	if ~isempty(query) && query(1) == 'y'		gamInverse = [0:255; 0:255; 0:255]'; %Use uncorrected gamma inverse function		dacsize = 8; %Use dacsize 8	else		error('Exiting program...');	endendeval(['cd ''' expdir '''']);